const {Optimizer} = require('@parcel/plugin');

module.exports = new Optimizer({
  async loadConfig({config}) {
    let res = await config.getConfig(['.optimizerrc']);
    config.setResult({
      value: res && res.contents.value
    });
  },
  optimize({config}) {
    return {
      contents: config.value || 'optimized'
    }
  }
});
